import React, { useState } from "react";

function FormPage() {
  const [teamName, setTeamName] = useState("");
  const [missingLaps, setMissingLaps] = useState("");
  const [timing, setTiming] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newData = {
      teamName,
      missingLaps: parseInt(missingLaps),
      timing: parseFloat(timing),
    };

    try {
      const response = await fetch('http://localhost:3001/data.json');
      const data = await response.json();
      data.push(newData);

      // Simulate writing to the JSON file
      // In a real application, you would send this data to a backend server.
      console.log(data);

      // Clear form fields
      setTeamName("");
      setMissingLaps("");
      setTiming("");
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <div>
      <h1>Enter Team Data</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Team Name:</label>
          <input
            type="text"
            value={teamName}
            onChange={(e) => setTeamName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Missing Laps:</label>
          <input
            type="number"
            value={missingLaps}
            onChange={(e) => setMissingLaps(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Timing:</label>
          <input
            type="number"
            value={timing}
            onChange={(e) => setTiming(e.target.value)}
            step="0.01"
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FormPage;
