import React, { useState } from 'react';
import axios from 'axios';

const AddTeamForm = () => {
    const [teamName, setTeamName] = useState('');
    const [timing, setTiming] = useState('');
    const [missingLaps, setMissingLaps] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:5002/add-team', {
            team_name: teamName,
            timing: parseFloat(timing),
            missing_laps: parseInt(missingLaps, 10)
        })
        .then(response => {
            alert(response.data);
        })
        .catch(error => {
            console.error(error);
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={teamName}
                onChange={(e) => setTeamName(e.target.value)}
                placeholder="Team Name"
                required
            />
            <input
                type="number"
                value={timing}
                onChange={(e) => setTiming(e.target.value)}
                placeholder="Timing"
                step="0.01"
                required
            />
            <input
                type="number"
                value={missingLaps}
                onChange={(e) => setMissingLaps(e.target.value)}
                placeholder="Missing Laps"
                required
            />
            <button type="submit">Add Team</button>
        </form>
    );
};

export default AddTeamForm;
