import React, { useState } from 'react';
import './App.css'
function FormPage() {
  const [teamName, setTeamName] = useState('');
  const [missingLaps, setMissingLaps] = useState('');
  const [timing, setTiming] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Retrieve existing data from local storage or create a new array if it doesn't exist
    const storedData = JSON.parse(localStorage.getItem('data')) || [];

    // Add new entry
    const newEntry = { teamName, missingLaps, timing };
    storedData.push(newEntry);

    // Save the updated data back to local storage
    localStorage.setItem('data', JSON.stringify(storedData));

    // Reset form fields
    setTeamName('');
    setMissingLaps('');
    setTiming('');
  };

  return (
    <div>
      <h2>Enter Team Details</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Team Name"
          value={teamName}
          onChange={(e) => setTeamName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Missing Laps"
          value={missingLaps}
          onChange={(e) => setMissingLaps(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Timing"
          value={timing}
          onChange={(e) => setTiming(e.target.value)}
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FormPage;
