import React, { useState } from 'react';

function FormPage() {
  const [teamName, setTeamName] = useState('');
  const [missingLaps, setMissingLaps] = useState('');
  const [timing, setTiming] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Fetch current data from JSON file
    const response = await fetch('http://localhost:3001/data.json');
    const data = await response.json();

    // Add new entry
    const newEntry = { teamName, missingLaps, timing };
    data.push(newEntry);

    // Save the updated data back to the file
    await fetch('http://localhost:3001/data.json', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    });

    // Reset form fields
    setTeamName('');
    setMissingLaps('');
    setTiming('');
  };

  return (
    <div>
      <h2>Enter Team Details</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Team Name"
          value={teamName}
          onChange={(e) => setTeamName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Missing Laps"
          value={missingLaps}
          onChange={(e) => setMissingLaps(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Timing"
          value={timing}
          onChange={(e) => setTiming(e.target.value)}
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default FormPage;
